import { ArrowLeftOutlined, DeleteOutlined, HomeOutlined, PlusOutlined } from '@ant-design/icons';
import {
  Badge,
  Breadcrumb,
  Button,
  Card,
  Empty,
  Image,
  Input,
  Modal,
  Select,
  Space,
  Typography,
  notification,
} from 'antd';
import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { CATEGORY_COLORS, NOTICE_FILTER_CATEGORIES, NOTICE_MESSAGES } from '../../../constants';
import { useLoading } from '../../../context/LoadingContext';
import { usePermission } from '../../../hooks/usePermission';
import { noticeService } from '../../../services/noticeService';
import { NOTIFICATION_CONFIG } from '../../../types/api';
import { Notice, NoticeCategory, NoticeCategoryFilter, NoticeFilters } from '../../../types/notice';

const { Title, Text, Paragraph } = Typography;
const { Search } = Input;

const NoticeBoard: React.FC = () => {
  const [notices, setNotices] = useState<Notice[]>([]);
  const [filters, setFilters] = useState<NoticeFilters>({ category: 'all' });
  const { startLoading, stopLoading } = useLoading();
  const [api, contextHolder] = notification.useNotification();
  const [modalApi, modalContextHolder] = Modal.useModal();
  const navigate = useNavigate();
  const { can } = usePermission();

  const fetchNotices = async (currentFilters: NoticeFilters = {}) => {
    try {
      console.log('[NoticeBoard] Starting fetch with filters:', currentFilters);
      startLoading();

      const apiFilters = {
        ...currentFilters,
        category: currentFilters.category === 'all' ? undefined : (currentFilters.category as NoticeCategory),
      };
      console.log('[NoticeBoard] Processed API filters:', apiFilters);

      const data = await noticeService.getNotices(apiFilters);
      console.log('[NoticeBoard] Received notices from service:', {
        received: !!data,
        length: data?.length || 0,
        isArray: Array.isArray(data),
      });

      if (!data) {
        console.warn('[NoticeBoard] No data received, setting empty array');
        setNotices([]);
        return;
      }

      console.log('[NoticeBoard] Setting notices state with:', {
        length: data.length,
        sample: data.slice(0, 2).map((n) => ({ id: n.id, title: n.title })),
      });
      setNotices(data);
    } catch (error: any) {
      console.error('[NoticeBoard] Error in fetchNotices:', error);
      api.error({
        message: NOTICE_MESSAGES.FETCH.ERROR,
        description: error.response?.data?.message || 'Please try again later.',
        ...NOTIFICATION_CONFIG,
      });
      setNotices([]);
    } finally {
      stopLoading();
    }
  };

  useEffect(() => {
    console.log('[NoticeBoard] useEffect triggered with filters:', filters);
    fetchNotices(filters);
  }, [filters]);

  // Add logging to render
  console.log('[NoticeBoard] Rendering with notices:', {
    length: notices.length,
    hasNotices: notices.length > 0,
    sample: notices.slice(0, 2).map((n) => ({ id: n.id, title: n.title })),
  });

  const handleCategoryChange = (category: NoticeCategoryFilter) => {
    setFilters((prev) => ({
      ...prev,
      category: category,
    }));
  };

  const handleSearch = (value: string) => {
    setFilters((prev) => ({
      ...prev,
      search: value,
    }));
  };

  const handleNoticeClick = (id: string) => {
    navigate(`/notices/${id}`);
  };

  const handleDelete = async (e: React.MouseEvent, notice: Notice) => {
    e.stopPropagation(); // Prevent card click when clicking delete button

    modalApi.confirm({
      title: 'Delete Notice',
      content: `Are you sure you want to delete "${notice.title}"?`,
      okText: 'Yes, Delete',
      okButtonProps: {
        danger: true,
        className: 'bg-red-500',
      },
      centered: true,
      cancelText: 'Cancel',
      onOk: async () => {
        try {
          startLoading();
          await noticeService.deleteNotice(notice.id);
          api.success({
            message: 'Notice Deleted',
            description: 'The notice has been successfully deleted.',
            ...NOTIFICATION_CONFIG,
          });
          fetchNotices(filters); // Refresh the list
        } catch (error: any) {
          api.error({
            message: 'Failed to Delete Notice',
            description: error.response?.data?.message || 'Please try again later.',
            ...NOTIFICATION_CONFIG,
          });
        } finally {
          stopLoading();
        }
      },
    });
  };

  return (
    <div className="p-6">
      {modalContextHolder}
      {contextHolder}
      <Breadcrumb className="mb-4">
        <Breadcrumb.Item>
          <Link to="/">
            <HomeOutlined />
          </Link>
        </Breadcrumb.Item>
        <Breadcrumb.Item>Notice Board</Breadcrumb.Item>
      </Breadcrumb>
      <div className="flex items-center justify-between mb-6">
        <Button
          type="link"
          icon={<ArrowLeftOutlined />}
          onClick={() => navigate('/dashboard')}
          className="text-white hover:text-blue-400"
        >
          Back to Dashboard
        </Button>
        {can('create:notice') && (
          <Button type="primary" icon={<PlusOutlined />} onClick={() => navigate('/notices/create')}>
            Create Notice
          </Button>
        )}
      </div>

      <div className="mb-6">
        <Title level={2} className="text-white mb-6">
          Notice Board
        </Title>
        <Space className="w-full justify-end mb-4">
          <Select
            placeholder="Filter by category"
            value={filters.category || 'all'}
            style={{ width: 200 }}
            options={NOTICE_FILTER_CATEGORIES}
            onChange={handleCategoryChange}
          />
          <Search placeholder="Search notices" allowClear onSearch={handleSearch} style={{ width: 300 }} />
        </Space>
      </div>

      {notices.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {notices.map((notice) => (
            <Card
              key={notice.id}
              className="bg-yt-light-gray cursor-pointer transition-transform transform hover:scale-[1.02] hover:shadow-lg relative group"
              onClick={() => handleNoticeClick(notice.id)}
            >
              <div className="flex justify-between items-start mb-2">
                <Title level={4} className="text-white mb-0">
                  {notice.title}
                </Title>
                {can('delete:notice') && (
                  <Button
                    type="text"
                    danger
                    size="large"
                    icon={<DeleteOutlined />}
                    onClick={(e) => handleDelete(e, notice)}
                    className="opacity-0 group-hover:opacity-100 transition-opacity absolute top-2 right-2 text-red-500 hover:text-red-600"
                  />
                )}
              </div>
              <Image
                width="100%"
                className="mb-4 rounded"
                src={notice?.image_url}
                preview={false}
                placeholder={
                  <Image
                    preview={false}
                    src={
                      notice?.image_url + '?x-oss-process=image/blur,r_50,s_50/quality,q_1/resize,m_mfit,h_200,w_200'
                    }
                  />
                }
                style={{ objectFit: 'cover' }}
                fallback="data:image/png;base64,"
              />
              <Paragraph className="text-gray-400 mb-4" ellipsis={{ rows: 2 }}>
                {notice.content}
              </Paragraph>
              <div className="flex justify-between items-center mt-auto">
                <Badge color={CATEGORY_COLORS[notice.category]} text={notice.category} />
                <Text className="text-gray-400">
                  {new Date(notice.created_at).toLocaleDateString('en-GB', {
                    day: '2-digit',
                    month: 'short',
                    year: 'numeric',
                  })}
                </Text>
              </div>
            </Card>
          ))}
        </div>
      ) : (
        <Empty description={<Text className="text-gray-400">No notices found</Text>} />
      )}
    </div>
  );
};

export default NoticeBoard;
