import { ArrowLeftOutlined, DeleteOutlined, FilePdfOutlined, HomeOutlined } from '@ant-design/icons';
import { Badge, Breadcrumb, Button, Card, Image, Modal, Tag, Tooltip, Typography, notification } from 'antd';
import React, { useEffect, useState } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import { CATEGORY_COLORS, NOTICE_MESSAGES } from '../../../constants';
import { useLoading } from '../../../context/LoadingContext';
import { usePermission } from '../../../hooks/usePermission';
import { noticeService } from '../../../services/noticeService';
import { NOTIFICATION_CONFIG } from '../../../types/api';
import { Notice } from '../../../types/notice';

const { Title, Text, Paragraph } = Typography;

interface Attachment {
  url: string;
  filename: string;
}

const NoticeDetails: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [notice, setNotice] = useState<Notice | null>(null);
  const { startLoading, stopLoading } = useLoading();
  const [api, contextHolder] = notification.useNotification();
  const [modalApi, modalContextHolder] = Modal.useModal();
  const [ellipsis, _setEllipsis] = useState(true);
  const navigate = useNavigate();
  const { can } = usePermission();

  useEffect(() => {
    const fetchNotice = async () => {
      if (!id) return;

      try {
        startLoading();
        const data = await noticeService.getNoticeById(id);
        setNotice(data);
      } catch (error: any) {
        api.error({
          message: NOTICE_MESSAGES.FETCH.SINGLE_ERROR,
          description: error.response?.data?.message || 'Please try again later.',
          ...NOTIFICATION_CONFIG,
        });
        navigate('/notice-board');
      } finally {
        stopLoading();
      }
    };

    fetchNotice();
  }, [id, navigate]);

  const handleDelete = async () => {
    if (!id || !notice) return;

    modalApi.confirm({
      title: 'Delete Notice',
      content: `Are you sure you want to delete "${notice.title}"?`,
      okText: 'Yes, Delete',
      okButtonProps: {
        danger: true,
        className: 'bg-red-500',
      },
      cancelText: 'Cancel',
      onOk: async () => {
        try {
          startLoading();
          await noticeService.deleteNotice(id);
          api.success({
            message: 'Notice Deleted',
            description: 'The notice has been successfully deleted.',
            ...NOTIFICATION_CONFIG,
          });
          navigate('/notice-board');
        } catch (error: any) {
          api.error({
            message: 'Failed to Delete Notice',
            description: error.response?.data?.message || 'Please try again later.',
            ...NOTIFICATION_CONFIG,
          });
        } finally {
          stopLoading();
        }
      },
    });
  };

  if (!notice) {
    return null;
  }

  return (
    <div className="p-6">
      {modalContextHolder}
      {contextHolder}
      <Breadcrumb className="mb-4">
        <Breadcrumb.Item>
          <Link to="/">
            <HomeOutlined />
          </Link>
        </Breadcrumb.Item>
        <Breadcrumb.Item>
          <Link to="/notice-board">Notice Board</Link>
        </Breadcrumb.Item>
        <Breadcrumb.Item>Notice Detail</Breadcrumb.Item>
      </Breadcrumb>
      <div className="max-w-3xl mx-auto">
        <Button
          type="link"
          icon={<ArrowLeftOutlined />}
          onClick={() => navigate('/notice-board')}
          className="mb-4 text-white hover:text-blue-400 p-0"
        >
          Back to Notice Board
        </Button>
        <Card className=" bg-yt-light-gray">
          <div className="flex justify-between items-start">
            <Title level={2} className="text-white mr-2 mb-0" ellipsis={ellipsis ? { tooltip: notice.title } : false}>
              {notice.title}
            </Title>
            {can('delete:notice') && (
              <Button
                style={{ padding: 20 }}
                type="text"
                danger
                size="large"
                icon={<DeleteOutlined />}
                onClick={handleDelete}
                className="text-red-500 hover:text-red-600"
              />
            )}
          </div>
          <Text className="text-gray-400 block mb-4">Posted by: {notice.posted_by}</Text>
          <div className="max-w-80">
            <Image
              width="100%"
              className="mb-4 rounded"
              src={notice?.image_url}
              preview={false}
              placeholder={
                <Image
                  preview={false}
                  src={notice?.image_url + '?x-oss-process=image/blur,r_50,s_50/quality,q_1/resize,m_mfit,h_200,w_200'}
                />
              }
              style={{ objectFit: 'cover' }}
              fallback="data:image/png;base64,"
            />
          </div>
          <Paragraph ellipsis={ellipsis ? { rows: 5, expandable: true, symbol: 'more' } : false}>
            {notice.content}
          </Paragraph>
          <div className="mb-4">
            <Text strong={true}>Attachments: </Text>
            {notice.attachments && (
              <Tooltip title={notice.attachments.filename} key={notice.attachments.filename}>
                <a
                  onClick={(e) => e.stopPropagation()}
                  href={notice.attachments.url}
                  download={notice.attachments.filename} // Use the download attribute
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <Button type="link" icon={<FilePdfOutlined />}>
                    Download PDF
                  </Button>
                </a>
              </Tooltip>
            )}
          </div>
          <div className="flex justify-between items-center">
            <Tag color={CATEGORY_COLORS[notice.category]}>
              {notice?.category?.charAt(0).toUpperCase() + notice?.category?.slice(1)}
            </Tag>
            <Text className="text-gray-400">
              Posted on{' '}
              {new Date(notice.created_at).toLocaleDateString('en-GB', {
                day: '2-digit',
                month: 'short',
                year: 'numeric',
              })}
            </Text>
          </div>
        </Card>
      </div>
    </div>
  );
};

export default NoticeDetails;
